const items = $input.all();
const results = [];

for (const item of items) {
  const input = item.json;
  const body = input.body || input;
  const call = body.call || body;
  const meta = call.metadata || {};

  const leadId = meta.lead_id || '';
  const durationSec = call.duration_ms ? Math.round(call.duration_ms / 1000) : 0;

  results.push({
    json: {
      lead_id: leadId,
      has_lead_id: leadId !== '' && leadId !== null && leadId !== undefined,
      retell_call_id: call.call_id || '',
      call_status: call.call_status || '',
      last_call_date: new Date().toISOString(),
      call_duration: String(durationSec),
      disconnection_reason: call.disconnection_reason || '',
      recording_url: call.recording_url || '',
      call_transcript: call.transcript || '',
    }
  });
}

return results;

const data = $input.item.json;

const update = {
  lead_id: data.lead_id,
  retell_call_id: data.retell_call_id,

  call_duration: String(data.call_duration),
  disconnection_reason: data.disconnection_reason,
  recording_url: data.recording_url,
  call_transcript: data.call_transcript,
  last_call_date: data.last_call_date,
};

return [{ json: update }];

const items = $input.all();
const results = [];
for (const item of items) {
  const input = item.json;

  const body = input.body || input;
  const call = body.call || body;
  const analysis = call.call_analysis || {};
  const custom = analysis.custom_analysis_data || {};
  const meta = call.metadata || {};
  const toolCalls = call.tool_calls || [];
  const phoneIndex = parseInt(meta.phone_index) || 1;
  const currentAttempts = parseInt(meta.attempt) || 1;
  const reason = call.disconnection_reason || 'unknown';

  // FIX 2: Read max_phone_index from metadata (fallback to 3 for old calls)
  const maxPhoneIndex = parseInt(meta.max_phone_index) || 3;

  // All times in US Eastern
  const TZ = 'America/New_York';
  const now = new Date();
  const nowET = new Date(now.toLocaleString('en-US', { timeZone: TZ }));
  const tzOffset = now.getTime() - nowET.getTime();

  // Helper: create a US Eastern date and convert to UTC ISO for storage
  const toUTCIso = (etDate) => new Date(etDate.getTime() + tzOffset).toISOString();

  // Per-phone attempt calculation (3 attempts per phone, not 3 global)
  const MAX_RETRIES_PER_PHONE = 3;
  const perPhoneAttempts = currentAttempts - ((phoneIndex - 1) * MAX_RETRIES_PER_PHONE);

  // Extract from call_analysis (from Retell AI)
  const callSummary = analysis.call_summary || '';
  const userSentiment = analysis.user_sentiment || '';
  const inVoicemail = analysis.in_voicemail || false;

  // Extract from custom_analysis_data (configured in Retell)
  const interestLevel = custom.interest_level || '';
  const meetingScheduledCustom = custom.meeting_scheduled || false;
  const emailCaptured = custom.email_captured || '';
  const phoneCaptured = custom.phone_captured || '';
  const objectionReason = custom.objection_reason || custom.objections || '';
  const callbackTime = custom.callback_time || '';
  const decisionMaker = custom.decision_maker || false;
  const painPoints = custom.pain_points || '';
  const nextSteps = custom.next_steps || custom.recommended_action || '';
  const reachedVoicemail = custom.reached_voicemail || false;

  // Check tool_calls for meeting/demo/email
  let meetingFromTools = '';
  let emailFromTools = '';
  let demoScheduled = '';
  toolCalls.forEach(tc => {
    const name = (tc.name || '').toLowerCase();
    if (name.includes('meeting') || name.includes('schedule') || name.includes('calendar')) {
      meetingFromTools = 'Yes';
    }
    if (name.includes('email') || name.includes('capture_email')) {
      emailFromTools = 'Yes';
    }
    if (name.includes('demo')) {
      demoScheduled = 'Yes';
    }
  });

  // Map disconnection reasons
  const reasonMap = {
    'user_hangup': { phoneStatus: 'connected', canRetry: false, tryNext: false, stopAll: false, connected: true },
    'agent_hangup': { phoneStatus: 'connected', canRetry: false, tryNext: false, stopAll: false, connected: true },
    'call_transfer': { phoneStatus: 'connected', canRetry: false, tryNext: false, stopAll: false, connected: true },
    'inactivity': { phoneStatus: 'connected', canRetry: false, tryNext: false, stopAll: false, connected: true },
    'max_duration_reached': { phoneStatus: 'connected', canRetry: false, tryNext: false, stopAll: false, connected: true },
    'voicemail_reached': { phoneStatus: 'voicemail', canRetry: true, delayHours: 4, tryNext: false, stopAll: false, connected: false },
    'machine_detected': { phoneStatus: 'voicemail', canRetry: true, delayHours: 4, tryNext: false, stopAll: false, connected: false },
    'dial_no_answer': { phoneStatus: 'no_answer', canRetry: true, delayHours: 2, tryNext: false, stopAll: false, connected: false },
    'registered_call_timeout': { phoneStatus: 'no_answer', canRetry: true, delayHours: 2, tryNext: false, stopAll: false, connected: false },
    'dial_busy': { phoneStatus: 'busy', canRetry: true, delayHours: 1, tryNext: false, stopAll: false, connected: false },
    'dial_failed': { phoneStatus: 'failed', canRetry: false, tryNext: true, stopAll: false, connected: false },
    'invalid_destination': { phoneStatus: 'invalid', canRetry: false, tryNext: true, stopAll: false, connected: false },
    'telephony_provider_permission_denied': { phoneStatus: 'failed', canRetry: false, tryNext: true, stopAll: false, connected: false },
    'telephony_provider_unavailable': { phoneStatus: 'failed', canRetry: false, tryNext: true, stopAll: false, connected: false },
    'sip_routing_error': { phoneStatus: 'failed', canRetry: false, tryNext: true, stopAll: false, connected: false },
    'user_declined': { phoneStatus: 'declined', canRetry: false, tryNext: false, stopAll: true, connected: false },
    'marked_as_spam': { phoneStatus: 'declined', canRetry: false, tryNext: false, stopAll: true, connected: false },
    'scam_detected': { phoneStatus: 'declined', canRetry: false, tryNext: false, stopAll: true, connected: false },
    'no_valid_payment': { phoneStatus: 'failed', canRetry: false, tryNext: false, stopAll: true, connected: false },
    'concurrency_limit_reached': { phoneStatus: 'retry', canRetry: true, delayHours: 0.5, tryNext: false, stopAll: false, connected: false }
  };

  const mapping = reasonMap[reason] || { 
    phoneStatus: 'unknown', 
    canRetry: false, 
    delayHours: 0,
    tryNext: false, 
    stopAll: false,
    connected: false
  };

  // FIX 1: Voicemail override â€” check before connected logic, override connected flag
  if (inVoicemail || reachedVoicemail) {
    mapping.phoneStatus = 'voicemail';
    mapping.connected = false;
    mapping.canRetry = true;
    mapping.delayHours = 4;
  }

  // Voicemail detection: combine all signals
  const isVoicemail = inVoicemail || reachedVoicemail || 
                      reason === 'voicemail_reached' || 
                      reason === 'machine_detected' ||
                      mapping.phoneStatus === 'voicemail';

  // Determine call_outcome, call_status, and next actions
  let callOutcome, callStatus, nextCallDate = '', nextPhoneIndex = phoneIndex;
  let callSuccessful = false;
  let followUpStatus = '';

  if (mapping.stopAll) {
    callOutcome = 'DECLINED';
    callStatus = 'not_interested';
    callSuccessful = false;
    followUpStatus = 'closed';
  } else if (mapping.connected) {
    callOutcome = 'CONNECTED';
    callStatus = 'completed';
    callSuccessful = true;
    followUpStatus = 'pending_review';
  } else if (mapping.tryNext) {
    if (phoneIndex < maxPhoneIndex) {
      nextPhoneIndex = phoneIndex + 1;
      callOutcome = 'FAILED';
      callStatus = 'scheduled';
      nextCallDate = toUTCIso(new Date(nowET.getTime() + 60 * 60 * 1000));
      followUpStatus = 'scheduled_callback';
    } else {
      callOutcome = 'FAILED';
      callStatus = 'exhausted';
      followUpStatus = 'exhausted';
    }
    callSuccessful = false;
  } else if (mapping.canRetry) {
    if (perPhoneAttempts >= MAX_RETRIES_PER_PHONE) {
      if (phoneIndex < maxPhoneIndex) {
        nextPhoneIndex = phoneIndex + 1;
        callOutcome = mapping.phoneStatus.toUpperCase();
        callStatus = 'scheduled';
        nextCallDate = toUTCIso(new Date(nowET.getTime() + 60 * 60 * 1000));
        followUpStatus = 'scheduled_callback';
      } else {
        callOutcome = mapping.phoneStatus.toUpperCase();
        callStatus = 'exhausted';
        followUpStatus = 'exhausted';
      }
    } else {
      callOutcome = mapping.phoneStatus.toUpperCase();
      callStatus = 'retry';
      nextCallDate = toUTCIso(new Date(nowET.getTime() + (mapping.delayHours || 2) * 60 * 60 * 1000));
      followUpStatus = 'scheduled_callback';
    }
    callSuccessful = false;
  } else {
    callOutcome = 'UNKNOWN';
    callStatus = 'retry';
    callSuccessful = false;
    followUpStatus = 'pending_review';
  }

  // Parse callback_time if prospect requested a callback (all in US Eastern)
  if (callbackTime && callStatus !== 'not_interested' && callStatus !== 'exhausted') {
    let parsedET = null;
    const cbLower = callbackTime.toLowerCase().trim();

    // Try ISO date first
    const isoDate = new Date(callbackTime);
    if (!isNaN(isoDate.getTime()) && isoDate > now) {
      parsedET = new Date(isoDate.toLocaleString('en-US', { timeZone: TZ }));
    }

    // "tomorrow at 3pm" / "tomorrow at 3:30 pm"
    if (!parsedET && cbLower.includes('tomorrow')) {
      const timeMatch = cbLower.match(/(\d{1,2})(?::(\d{2}))?\s*(am|pm)/i);
      if (timeMatch) {
        parsedET = new Date(nowET);
        parsedET.setDate(parsedET.getDate() + 1);
        let hours = parseInt(timeMatch[1]);
        const minutes = parseInt(timeMatch[2] || '0');
        if (timeMatch[3].toLowerCase() === 'pm' && hours < 12) hours += 12;
        if (timeMatch[3].toLowerCase() === 'am' && hours === 12) hours = 0;
        parsedET.setHours(hours, minutes, 0, 0);
      }
    }

    // "in 2 hours" / "in 30 minutes"
    if (!parsedET) {
      const relMatch = cbLower.match(/in\s+(\d+)\s*(hour|minute|min)/i);
      if (relMatch) {
        const amount = parseInt(relMatch[1]);
        const unit = relMatch[2].toLowerCase();
        const ms = unit.startsWith('hour') ? amount * 3600000 : amount * 60000;
        parsedET = new Date(nowET.getTime() + ms);
      }
    }

    // Bare time "3 PM" / "3:30 PM" (assumes today ET if future, otherwise tomorrow ET)
    if (!parsedET) {
      const bareTime = cbLower.match(/^(\d{1,2})(?::(\d{2}))?\s*(am|pm)$/i);
      if (bareTime) {
        parsedET = new Date(nowET);
        let hours = parseInt(bareTime[1]);
        const minutes = parseInt(bareTime[2] || '0');
        if (bareTime[3].toLowerCase() === 'pm' && hours < 12) hours += 12;
        if (bareTime[3].toLowerCase() === 'am' && hours === 12) hours = 0;
        parsedET.setHours(hours, minutes, 0, 0);
        if (parsedET <= nowET) {
          parsedET.setDate(parsedET.getDate() + 1);
        }
      }
    }

    if (parsedET) {
      callStatus = 'callback_scheduled';
      nextCallDate = toUTCIso(parsedET);
      followUpStatus = 'scheduled_callback';
    }
  }

  const durationSec = call.duration_ms ? Math.round(call.duration_ms / 1000) : 0;
  const callCost = call.call_cost?.combined_cost || 0;
  const leadId = meta.lead_id || '';

  // Determine if HOT lead for alerts
  const isHot = (interestLevel === 'HOT' || 
                 interestLevel === 'CALLBACK' || 
                 meetingScheduledCustom === true || 
                 meetingFromTools === 'Yes');

  results.push({
    json: {
      // Identification
      lead_id: leadId,
      has_lead_id: leadId !== '',

      // Call metadata
      agent_id: call.agent_id || '',
      phone_index: phoneIndex,
      phone_status: mapping.phoneStatus,
      current_phone_index: String(nextPhoneIndex),
      call_status: callStatus,
      call_outcome: callOutcome,
      call_attempts: String(currentAttempts),
      disconnection_reason: reason,
      call_successful: callSuccessful ? 'Yes' : 'No',
      call_duration: String(durationSec),
      call_transcript: call.transcript || '',
      recording_url: call.recording_url || '',
      next_call_date: nextCallDate,
      call_cost: String(callCost.toFixed(4)),

      // AI Analysis fields
      call_summary: callSummary,
      user_sentiment: userSentiment,
      interest_level: interestLevel,
      objection_reason: objectionReason,
      callback_time: callbackTime,
      decision_maker: decisionMaker ? 'Yes' : '',
      pain_points: painPoints,
      next_steps: nextSteps,
      follow_up_status: followUpStatus,
      meeting_scheduled: meetingScheduledCustom ? 'Yes' : meetingFromTools,
      email_captured: emailCaptured || emailFromTools,
      phone_captured: phoneCaptured,
      demo_scheduled: demoScheduled,
      reached_voicemail: isVoicemail ? 'Yes' : 'No',

      // Hot lead flag for alerts
      is_hot: isHot ? 'TRUE' : 'FALSE'
    }
  });
}
return results;
